import { styled } from "styled-components";
import CardContent  from "../../reactComponents/card/Card";
import  Typography   from "../../reactComponents/typography/Typography";

export const ContainerStyled = styled.div`
  padding: 20px;
  margin: 0 auto;
  max-width: 1024px;
`

export const LabelStyle = styled(Typography)`
font-weight: 500;
font-size: 1.5rem;
`
export const ValueStyle = styled(Typography)`
font-weight: 400;
font-size: 2.5rem;
`
export const CardPanelContantStyle = styled(CardContent)`
display: flex;
justify-content: space-between;
padding: 25px;
`
export const ItemStyle = styled.div`
display: flex;
justify-content: space-between;
min-width: 150px;
`
