import styled from 'styled-components'
import  CardContent from '../../../reactComponents/card/Card'
import Typography from '../../../reactComponents/typography/Typography'

export const LabelStyle = styled(Typography)`
  font-weight: 500;
  font-family: 'Times New Roman', Times, serif;
  font-size: 1.5rem;
  color: #7CFC00;
 
`
export const ValueStyle = styled(Typography)`
  font-weight: 400;
  font-size: 2.5rem;
  color: burlywood;
  font-family: 'Times New Roman', Times, serif;
`

export const CardContentStyle = styled(CardContent)`
  border-left: 8px solid ${({ color }) => color || '#5d78ff'};
  background-color: #1C1C1C;
`

export const CardPanelContentStyle = styled(CardContent)`
  display: flex;
  justify-content: space-between;
  padding: 25px;
  
`

export const ItemStyled = styled.div`
  display: flex;
  justify-content: space-between;
  min-width: 150px;
`
