import React, {memo} from 'react';
import PropTypes  from 'prop-types';
import Grid  from '../../../reactComponents/grid/Grid';
import  Skeleton  from '../../../reactComponents/skeleton/Skeleton';
import Card from './Card'

function Board({dados}){
    const {casos,todayDeaths, recovered, deaths, todayCases} = dados
    const getValues =(value) => value ? value: <Skeleton variant="text" width={200} height={30}/>
    if(casos > 10000){
        return (
            <Grid container spacing={4}>
             <Grid item xs={12} md={3}>
                 
              <Card value={(casos)} label="Total de casos no Pais" color="#b41a06" />
              </Grid>   
             </Grid>
    
        )
    }else{
    return (
        <Grid container spacing={4}>
         <Grid item xs={12} md={3}>
             
          <Card value={(casos)} label="Total de casos no Pais" color="#5d78ff" />
          </Grid>   
         </Grid>

    )
    }
}
export default memo(Board)
