import React, { memo, useState, useCallback, useEffect } from 'react'
import Api from '../../App'
import Board from './components/Board'
import Panel from './components/Panel'
import {ContainerStyled}  from './Main-Style'


function Main() {
  const [dados, setDados] = useState({})
  const [pais, setPais] = useState('brazil')
  const updateAt = new Date().toLocaleString()
 
  const getDadosCovid = useCallback((pais) => {
    Api.getPais(pais)
      .then(dados => setDados(dados))
  }, [])

  useEffect(() => {    
    getDadosCovid(pais)
  }, [getDadosCovid, pais])
  
  const hendleChange = ({target})=> {
  const pais = target.value 
  setPais(pais) 
}
  return (
    <ContainerStyled>
        <div className="mb-2">
        <Panel
         dados={dados}
         updateAt={updateAt}
         onChenge={hendleChange}
         pais={pais}
         getDadosCovid={getDadosCovid} 
         />
        </div>
        <Board dados ={dados} />
        </ContainerStyled>
  )
      
}
export default memo(Main);
