import React ,{memo} from "react";
import RefreshIcon from '../../../imgs/RefreshIcon.svg'
import {Card, MenuItem, Typography, Button} from '../../../reactComponents'
import Select from '../../../reactComponents/Select'
import PAISES from '../../../utilitiesStyles/constants/paises'
import {CardPanelContentStyled, ItemStyled } from './style'



const NavigatorHasShere = navigator.share

function Panel({ updateAt, onChange, dados, pais,getDadosCovid}){
    const {cases,recovered,deaths,todayCases,todayDeaths} = dados

    const renderPaises = (pais, index) => (
        <MenuItem key={`Pais-${index}`} value={pais.value}>
          <ItemStyled>
            <div>{pais.label}</div>
            <img src={pais.flag} alt={`País-${pais.label}`} />
          </ItemStyled>
        </MenuItem>
      )
      const txtCovid19 = `Pais-${pais}`
      const copyInfo = () => {
          navigator.clipboard.writeText(txtCovid19)
      }
      const sherInfo = () =>{
        navigator.share(
            {
                title: `Dados covid19-${dados}`,
                text: txtCovid19,
                url: `https://covid19dio.netfly.app`
            }
        )
      }
     const renderShereButton = (
       <div>
         <Button variant="contained" color="peimary" onClick={sherInfo}>
           Compartilhar
         </Button>
       </div>
     )
     const renderCopyButton = (
       <div>
         <Button variant="container" color="primary" onClick={copyInfo}>
           Copiar
         </Button>
       </div>
     )
    return (
        <Card>
            <CardPanelContentStyled>
                <div>
                    <Typography variant="h5" component="span" color="primary"> COVIDI-19 </Typography>
                    <Typography variant="h6" component="span" color="primary"> Painel Coronavírus </Typography>
                    <Typography variant="body2" component="span" color="primary"> Atualizações em: {updateAt} </Typography>
               
                <div className="pt-2">
                    <Select onChange={onChange} value={pais}>
                    { PAISES.map(renderPaises)}
                    </Select>
                </div>
                </div>
                {NavigatorHasShere ? renderShereButton: renderCopyButton}
            </CardPanelContentStyled>
        </Card>
    )
}  
export default memo(Panel)
