import React, { memo, useState, useCallback, useEffect } from 'react'
import Api from '../../api'
import {Board} from './components/Board'
import {ContainerStyled} from './style.js'


function Main() {
  const [dados, setDados] = useState({})
  const [pais, setPais] = useState('USA')
  const getDadosCovid = useCallback((pais) => {
    Api.getPais(pais)
      .then(dados => setDados(dados))
  }, [])

  useEffect(() => {    
    getDadosCovid(pais)
  }, [getDadosCovid, pais])
  return (
    <ContainerStyled>
        <div>

        </div>
        <Board dados ={ dados} />
        </ContainerStyled>
  )
      
};
export default memo(Main);
